      SUBROUTINE RCMODEN(SCHEME, MAXLEV)

      INTEGER ID, NENT, COLMOD, ISH, MAXLEV
      REAL MXL
      CHARACTER CHOICE*15, SCHEME*3

      DATA CHOICE/'HLSRGBCMYBWABWS'/
      DATA COLMOD/ 3 /

      I= INDEX(CHOICE,SCHEME)/3 + 1
c      CALL RQVMOD(ID,NENT,COLMOD,ISH,MXL)
      ID= 1
      CALL RVMODE(ID,0,COLMOD,I,FLOAT(MAXLEV))

      RETURN
      END
