       SUBROUTINE STARDIS(X,Y,HGT,RATIO)

        HGT2=RATIO*HGT/2.0
        THICKL=RATIO*0.2/25.4
        YPH=Y+HGT2
        YMH=Y-HGT2
        XP=X+THICKL
        XM=X-THICKL
        CALL PLOT(XP,YPH,3)
        CALL PLOT(XM,YMH,2)
        CALL PLOT(XP,YMH,2)
        CALL PLOT(XM,YPH,2)
        CALL PLOT(XP,YPH,2)
        XMH=X-HGT2
        XPH=X+HGT2
        YM=Y-THICKL
        YP=Y+THICKL
        CALL PLOT(XMH,YP,3)
        CALL PLOT(XPH,YM,2)
        CALL PLOT(XPH,YP,2)
        CALL PLOT(XMH,YM,2)
        CALL PLOT(XMH,YP,2)
        CALL PLOT(XMH,Y,3)
        CALL PLOT(XPH,Y,2)
  
        HGT2=HGT2*0.7071
        THICKL=THICKL*0.7071
        XMH=X-HGT2
        XPH=X+HGT2
        YMH=Y-HGT2
        YPH=Y+HGT2
        CALL PLOT(XMH-THICKL,YPH-THICKL,3)
        CALL PLOT(XPH+THICKL,YMH+THICKL,2)
        CALL PLOT(XPH-THICKL,YMH-THICKL,2)
        CALL PLOT(XMH+THICKL,YPH+THICKL,2)
        CALL PLOT(XMH-THICKL,YPH-THICKL,2)
        CALL PLOT(XMH,YPH,3)
        CALL PLOT(XPH,YMH,2)

        CALL PLOT(XMH-THICKL,YMH+THICKL,3)
        CALL PLOT(XPH+THICKL,YPH-THICKL,2)
        CALL PLOT(XPH-THICKL,YPH+THICKL,2)
        CALL PLOT(XMH+THICKL,YMH-THICKL,2)
        CALL PLOT(XMH-THICKL,YMH+THICKL,2)
        CALL PLOT(XPH,YPH,3)
        CALL PLOT(XMH,YMH,2)
      RETURN
      END
