      SUBROUTINE YAXLOG(ISKIP)
      CHARACTER*3 YBTYPE
      CHARACTER*80 TITLEY
      COMMON /YAX/Y1,YL,YUP,YDOWN,YSCALE,YINC,DY,
     % Y1PL,YLPL,NY,Y1GRID,YLGRID,DIVY,YVAL(100),NYVAL
      COMMON /YAXC/TITLEY, YBTYPE
      COMMON /PARA/LABPT,NSM,NDIV,CAY,NARC,NRNG,HGTPT,HGT,
     % LABC(51),LWGT(51)
C
      
      DO 1000   I = 1, NYVAL
      IF( MOD(YVAL(I),1.0) .NE. 0.0 )   THEN
       NDEC= 1
       GO TO 1200
      END IF
 1000 CONTINUE
      NDEC= -1
 1200 CONTINUE
C
      ABSINC=ABS(YINC)
      YAXIS=ABS(ALOG(YUP/YDOWN)/ALOG(2.0))*YSCALE/2.54
      DY=YAXIS/ALOG10(YVAL(NYVAL)/YVAL(1))
      CALL PLOT(0.,0.,3)
      XMIN=0.0
      DO   10   I=1,NYVAL
      Y=(ALOG(YVAL(I)/YVAL(1))/ALOG(2.0))*YSCALE*DIVY/2.54
      CALL PLOT(0.,Y,2)
      CALL PLOT(0.1,Y,2)
      IF(MOD(I-1,ISKIP).NE.0)   GO TO 10
      YNUMB=YVAL(I)
      YN=ABS(YNUMB)
      IF(YN.LT.10.0)   YN=1.
      N=ABS(ALOG10(YN))+3+NDEC
      X=-N*HGT
      XMIN= MIN(XMIN,X)
      CALL NUMBER(X,Y,HGT,YNUMB,0.,NDEC)
   10 CALL PLOT(0.,Y,3)
      CALL PLOT(0.,YAXIS,2)
C  COUNT OF NON-BLANK WORDS OF TITLEY
      CALL CHARA(TITLEY,N)
      HGTCH=1.5*HGT
      Y=(YAXIS-N*HGTCH)/2. + HGTCH/2.0
      CALL SYMBOL(XMIN - HGT,Y,HGTCH,TITLEY,90.,N)
      RETURN
      END
