      SUBROUTINE CGXSMB (X,Y,HEIGHT,CHTEXT,THETA,N)
C     ------------------------------------------------------------------
C
C     *** JS   13-FEB-84      Integrate into DISPLA.
C     *** JS   20-FEB-84      Modify for DISPLA use.
C
C     FORTRAN IV IBM S/370 VERSION - 1980-06-04- ARNE LUND, NEUCC
C     ORIGINAL VERSION BY - N.WOLCOTT, NBS, 301-921-3384
C
C     ------------------------------------------------------------------
C
C     SIMILAR TO ROUTINE SYMBOL USED WITH PEN PLOTTERS
C     INSTRUCTIONS FOR DRAWING CHARACTERS ARE IN ALFBET STORED
C     TWO INSTRUCTIONS PER WORD. A ZERO FOR PEN POSITION INDICATES
C     END OF A CHARACTER. INPUT TEXTUAL STRINGS MUST BE COMPOSED OF ONLY
C     THE FORTRAN CHARACTERS A-Z 0-9+-/=()*,.X  AND THE NON-FORTRAN
C     SPECIAL CHARACTERS WHICH ARE DESCRIBED BELOW.
C        X,Y   =  (X,Y) COORDINATE OF LOWER LEFT HAND CORNER OF TEXT
C        HEIGHT =  HEIGHTH (IN INCHES) OF UPPER CASE CHARACTERS
C        TEXT   =  INPUT TEXTUAL ARRAY OR HOLLERITH STRING
C        THETA  =  ANGLE IN DEGREES OF TEXT FROM X-AXIS
C        N      =  NUMBER OF CHARACTERS IN TEXT STRING
C
C     IF N IS NEGATIVE AND TEXT IS A BINARY INTEGER BETWEEN
C     1 AND 21 A CENTERED SYMBOL FROM THE SYMBOL SET IS PRODUCED
C     AT (X,Y).
C     IF N IS 0 A SINGLE SIMPLEX ALPHANUMERIC FORTRAN CHARACTER
C     (RIGHT JUSTIFIED IN TEXT) IS PRODUCED AT (X,Y).
C     IF X AND/OR Y EQUAL 999. THE ANNOTATION WILL BE CONTINUED
C     AFTER THE PREVIOUS X AND/OR Y TERMINATION
C
C     ISTART(I) CONTAINS THE INDEX INTO ALFBET OF THE FIRST INSTRUCTION
C     OF CHARCTER NUMBER I WHOSE WIDTH IS STORED IN WIDTH(I)
C     CHARACTERS 1 TO 26 ARE ROMAN SIMPLEX UPPER CASE
C     CHARACTERS 27 TO 50 ARE GREEK SIMPLEX UPPER CASE
C     CHARACTERS 51 TO 76 ARE ROMAN SIMPLEX LOWER CASE
C     CHARACTERS 77 TO 100 ARE GREEK SIMPLEX LOWER CASE
C     CHARACTERS 101 TO 126 ARE ROMAN COMPLEX UPPER CASE
C     CHARACTERS 127 TO 150 ARE GREEK COMPLEX UPPER CASE
C     CHARACTERS 151 TO 176 ARE ROMAN COMPLEX LOWER CASE
C     CHARACTERS 177 TO 200 ARE GREEK COMPLEX LOWER CASE
C     CHARACTERS 201 TO 226 ARE ITALIC UPPER CASE
C     CHARACTERS 227 TO 250 ARE MATHEMATICAL AND SPECIAL SYMBOLS
C     CHARACTERS 251 TO 276 ARE ITALIC LOWER CASE
C     CHARACTERS 277 TO 300 ARE MATHEMATICAL AND SPECIAL SYMBOLS
C     CHARACTERS 301 TO 320 ARE SIMPLEX DIGITS AND SPECIAL SYMBOLS
C     CHARACTERS 321 TO 340 ARE COMPLEX DIGITS AND SPECIAL SYMBOLS
C     CHARACTERS 341 TO 360 ARE ITALIC DIGITS AND SPECIAL SYMBOLS
C     CHARACTERS 361 TO 380 ARE DUPLEX DIGITS AND SPECIAL SYMBOLS
C     CHARACTERS 381 TO 432 ARE DUPLEX UPPER AND LOWER CASE
C     ISPEC(13) ARE THIRTEEN SPECIAL CHARACTERS CHOSEN FROM THE NON-
C     FORTRAN SET AT JOUR SITE. CURRENT IMPLEMENTATIONS FOLLOW
C     SP11 MAY BE SET TO THE COMPUTER STOP CODE IF ONE EXISTS.
C     SP1   CHAR(14)      TURN ON CHARACTER SUBSCRIPT
C     SP2   CHAR(15)      TURN ON CHARACTER SUPERSCRIPT
C     SP3   CHAR(16)      TURN ON UPPER CASE
C     SP4   CHAR(17)      TURN ON LOWER CASE
C     SP5   CHAR(18)      TURN ON COMPLEX LETTERS
C     SP6   CHAR(19)      TURN ON CHARACTER GREEK LETTER OR MATH SYMBOL
C     SP7   CHAR(20)      TURN ON SIMPLEX LETTERS
C     SP8   CHAR(21)      BACK SPACE LAST CHARACTER
C     SP9   CHAR(22)      ITALIC LETTERS
C     SP10  CHAR(23)      DUPLEX LETTERS
C     SP11  CHAR(24)      TERMINATE SCAN
C     SP12  CHAR(25)      END SUBSCRIPT OR SUPERSCRIPT
C     SP13  CHAR(26)      END GREEK
C
C     ------------------------------------------------------------------
C
      DIMENSION IFORT(47),IA(256),IB(60),ISPEC(13)
      DIMENSION ALFBET(4711),WIDTH(432),ISTART(432),ISYMBO(128)
      DIMENSION JSTSYM(22)
      DIMENSION OLDX(6),OLDY(6)
      COMMON /G99ALF/ WIDTH,ALFBET,ISTART,ISYMBO,JSTSYM
c      COMMON /G99SAV/ XL,OLDX,OLDY,IA,IB,NWORD,NCHAR,NCWD,IOUT,
c     2                FIRST,LNGTH
      LOGICAL FIRST,LNGTH
      COMMON /G99SAV/ XL,OLDX,OLDY,IA,IB,NWORD,NCHAR,NCWD,IOUT
      COMMON /G99LOG/ FIRST,LNGTH
C*    INTEGER STRIN,CHAR,TEXT,ALFBET,ISTART
      INTEGER ALFBET,ISTART
      EQUIVALENCE (IB(1),IFORT(1)), (IB(48),ISPEC(1))
C/    INTEGER CHXCVT
      CHARACTER CHTEXT(N)
C     RX=CX-SY
C     RY=SX+CY
C     RX(X,Y)=RY(-Y,X)
C     RY(X,Y)=RX(Y,-X)
C     IFLD GETS N BITS STARTING AT NSTART TH BIT FROM RIGHT
      IFLD(NSTART,NBITS,IWORD)=MOD(IWORD/(2**(NSTART-NBITS)),2**NBITS)+(
C    2(1-ISIGN(1,+IWORD))/2)*(2**NBITS-1)
C     ***USE ABOVE CARD IF YOUR MACHINE HAS ONES COMPLEMENT ARITHMETIC
     2  (1-ISIGN(1,IWORD))/2)*(2**NBITS-MIN0(1,
     XMOD(-IWORD,2**(NSTART-NBITS))))
C     ***USE ABOVE CARD IF YOUR MACHINE HAS TWOS COMPLEMENT ARITHMETIC
C     ICHAR(J) GETS THE J TH CHARACTER OF NCHAR BITS FROM A STRING
C     COMPOSED OF WORDS NWORD BITS LONG.
C*    ICHAR(J)=IFLD((NWORD-MOD((J-1),NCWD)*NCHAR),NCHAR,STRIN((J+NCWD-1)/
C*   2NCWD,TEXT))
C/      ICHAR(J) = IFLD( NWORD - MOD(J-1,NCWD) * NCHAR, NCHAR,
C/     2                CGXG99((J+NCWD-1)/NCWD,TEXT) )
      R(XX,YY)=CO*XX-SI*YY
C
C     ***Start of actual code.
5     IF (FIRST) THEN
           NCWD=NWORD/NCHAR
           ICHSTA=-1
           IF (IFLD(NWORD,NCHAR,IFORT(1)).EQ.65) ICHSTA=NWORD
           IF (IFLD(NCHAR,NCHAR,IFORT(1)).EQ.65) ICHSTA=NCHAR
           IF (ICHSTA.EQ.-1) STOP 'HERSHEY SYSTEM ERROR 1'
           DO 178 I=1,13
C/                ISPEC(I)=CHXCVT(CHAR(ISPEC(I)))
                IA(ISPEC(I)+1)=47+I
 178            CONTINUE
           DO 10 I=1,60
                LL=IFLD(ICHSTA,NCHAR,IB(I))
                IA(LL+1)=I
10              CONTINUE
           FIRST=.FALSE.
           ENDIF
      THE=THETA*355./(180.*113.)
      SI=SIN(THE)
      CO=COS(THE)
      SCALE=HEIGHT/21.
      RSCALE=SCALE
C     ***OFFSET OF 11 UNITS
      X1=X
      Y1=Y
      IF (X1.GT.998.5) X1=OLDX(6)
      IF (Y1.GT.998.5) Y1=OLDY(6)
      NN=N
      IF (NN) 50,30,120
C
C     *** N is 0 - Single SIMPLEX alphanumeric character.
30    ICHR=NCWD-1
      IG=ICHAR(CHTEXT(ICHR+1))
      IC=IA(IG+1)
      IF (IC.LE.47) GO TO 40
C     WRITE(IOUT,40) IG
      GO TO 440
40    NN=1
      GO TO 140
C
C     *** N is negative - symbol case
50    IF (N.EQ.-1) GO TO 60
      CALL CGXPLT (X1,Y1,2)
60    IG=ICHAR(CHTEXT(1))+1
      IF (IG.LE.22) GO TO 70
C     WRITE(IOUT,41) IG
      GO TO 440
70    I6=JSTSYM(IG)
      I5=30
C
C     ***Loop outputting symbol
 80   CONTINUE
           I8=IFLD(I5,3,ISYMBO(I6))
C          *** GO TO CENTER OF SYMBOL
           IF (I8) 100,90,100
 90             CALL CGXPLT (X1,Y1,3)
                X1=X1+20.
                Y1=Y1+20.
                GO TO 440
C
 100       IX=IFLD(I5-3,6,ISYMBO(I6))
           IY=IFLD(I5-9,6,ISYMBO(I6))
           XI=SCALE*(FLOAT(IX-32))
           YI=SCALE*(FLOAT(IY-32))
           CALL CGXPLT (X1+R(XI,YI),Y1+R(YI,-XI),I8)
           I5=45-I5
           IF (I5.EQ.30) I6=I6+1
           GO TO 80
C
C     *** N is positive - normal character output
 120  DO 130 I=1,6
           OLDX(I)=X
           OLDY(I)=Y
 130       CONTINUE
      ICHR=0
C
C     ***Character
 140  IC=0
C
C     ***Simplex capital letters
      IP=0
      ICAP=0
      ICOMP=0
      ITALIC=0
      IDUPLE=0
C
C     ***Not Greek letters
      IGREEK=0
C
C     ***Print symbol on line (not subscript or superscript)
      L=1
      SCALE=HEIGHT/21.
      DO 430 I1=1,NN
C
C          ***Extract character from text.
           ICHR=ICHR+1
           IG=ICHAR(CHTEXT(ICHR))
           IC=IA(IG+1)
           IF (ICHR.GT.NN.AND.N.GT.0) GO TO 450
           IF (IC.GT.60) THEN
                WRITE (IOUT,150) IG
 150                FORMAT (1X,19HILLEGAL INPUT CHAR ,I12)
                GO TO 430
                ENDIF
           IP=ICOMP+ICAP+ITALIC+IDUPLE
           IF (IC-27) 170,410,270
C
C               ***Number of symbol
 170            CONTINUE
                IC=IC+IP+IGREEK-ICAP*IDUPLE*24/(50*380)
 180            I6=ISTART(IC)
                I5=30
                IF (LNGTH) GO TO 250
C               ***Print alphabetic character
 190            I8=IFLD(I5,3,ALFBET(I6))
C
C               *** Test for end of character, I8=0
                IF (I8.EQ.0) GO TO 240
                IX=IFLD(I5-3,6,ALFBET(I6))
                IY=IFLD(I5-9,6,ALFBET(I6))
C
C               *** If L=1:   Print symbol on line.
C               ***    L=2:   Subscript.
C               ***    L=3:   Superscript.
                GO TO (200,210,220), L
C
C                    *** Print symbol on line.
 200                 RSCALE=SCALE
                     YOFF=0.
                     GO TO 230
C
C                    ***Subscript
210                  RSCALE=SCALE*0.5
                     YOFF=-HEIGHT/4.
                     GO TO 230
C
C                    *** Superscript
 220                 RSCALE=SCALE*0.5
                     YOFF=HEIGHT*3./4.
C               ABOVE FACTORS NEEDED TO CORRECT FOR DIFFERENT SIZE LETTERS
 230            YI=FLOAT(IY-11)*RSCALE+YOFF
                XI=FLOAT(IX-10)*RSCALE
                CALL CGXPLT (X1+R(XI,YI),Y1+R(YI,-XI),I8)
                I5=45-I5
                IF (I5.EQ.30) I6=I6+1
                GO TO 190
C
C 240       IGREEK=0
 240  CONTINUE
C*NEUCC    *** DEFINE SCALE WHEN USING CGXSML (LNGTH = .TRUE.)
250        CONTINUE
           RSCALE = SCALE
           IF (L .GT. 1) RSCALE = .5 * SCALE
           X1=X1+R(WIDTH(IC)*RSCALE,0.0)
           Y1=Y1+R(0.,-WIDTH(IC)*RSCALE)
           DO 260 I=1,5
                OLDX(I)=OLDX(I+1)
                OLDY(I)=OLDY(I+1)
 260            CONTINUE
           OLDX(6)=X1
           OLDY(6)=Y1
           RSCALE=SCALE
C          L=1
           GO TO 430
C
 270       IF (IC.GT.47) GO TO 280
           IC=IC+273
           IF (ICOMP.NE.0) IC=IC+20
           IF (ITALIC.NE.0) IC=IC+40
           IF (IDUPLE.NE.0) IC=IC+60
           GO TO 180
C
280        L1=IC-47
C
C          *** Switch on new char type
           GOTO (290,300,310,320,330,340,350,360,380,390,400,405,407),L1
C
C               ***Subscript
290             L=2
                GO TO 430
C
C               *** Superscript
300             L=3
                GO TO 430
C
C               *** Upper case
310             ICAP=0
                GO TO 430
C
C               *** Lower case
320             ICAP=50
                GO TO 430
C
C               *** Complex letters
330             ICOMP=100
                ITALIC=0
                IDUPLE=0
                GO TO 430
C
C               *** Greek letters
340             IGREEK=26
                IF (IDUPLE.NE.0) IGREEK=0
                GO TO 430
C
C               *** Simplex letters
350             ICOMP=0
                ITALIC=0
                IDUPLE=0
                GO TO 430
C
C               *** Backspace
360             DO 370 I=1,5
                     IK=6-I
                     OLDX(IK+1)=OLDX(IK)
370                  OLDY(IK+1)=OLDY(IK)
                X1=OLDX(6)
                Y1=OLDY(6)
                GO TO 430
C
C               *** Italic
380             ITALIC=200
                ICOMP=0
                IDUPLE=0
                GO TO 430
C
C               *** Duplex
390             IDUPLE=380
                ICOMP=0
                ITALIC=0
                GO TO 430
C
C               *** End of Scan
400             GO TO 450
C
C               *** End subscript or superscript
 405            CONTINUE
                L=1
                GOTO 430
C
C               *** End of Greek or Special character
 407            CONTINUE
                IGREEK=0
                GOTO 430
C
C               Blank Space
 410            X1=X1+R(20.*RSCALE,0.)
                Y1=Y1+R(0.,-20.*RSCALE)
                DO 420 I=1,5
                     OLDX(I)=OLDX(I+1)
420                  OLDY(I)=OLDY(I+1)
                OLDX(6)=X1
                OLDY(6)=Y1
CXX             L=1
 430       CONTINUE
C
 440  CONTINUE
      IF(LNGTH) GO TO 450
      OLDX(6)=X1
      OLDY(6)=Y1
 450  IF (LNGTH) XL=SQRT((X1-X)**2+(Y1-Y)**2)
      RETURN
      END
C
C ****************************************************************
C
      FUNCTION   CGXSML (HEIGHT,CHTEXT,N)
C
C*     LENGTH OF TEXT IS RETURNED AS RESULT
C
      DIMENSION IA(256),IB(60),OLDX(6),OLDY(6)
c      COMMON /G99SAV/ XL,OLDX,OLDY,IA,IB,NWORD,NCHAR,NCWD,IOUT,
c     2                FIRST,LNGTH
      LOGICAL LNGTH,FIRST
      COMMON /G99SAV/ XL,OLDX,OLDY,IA,IB,NWORD,NCHAR,NCWD,IOUT
      COMMON /G99LOG/ FIRST,LNGTH
      CHARACTER*(*) CHTEXT
C
      LNGTH=.TRUE.
      CALL CGXSMB (0.,0.,HEIGHT,CHTEXT,0.,N)
      CGXSML = XL
      LNGTH=.FALSE.
      RETURN
      END
C
