C ****************************************************************
C
C     *** INTERFACE TO HERSHEY CHARACTER GENERATOR
C
      SUBROUTINE CGXTYP(I)
      CHARACTER CCH(4)*1
      COMMON /HERSH/ ITYP,CCH
C
      IF (I.GE.1.AND.I.LE.4) ITYP=I
      RETURN
      END
C
C ****************************************************************
C
      SUBROUTINE CGXSMX(X,Y,H,TEXT,THETA,N)
      DIMENSION IDUMMY(21)
      CHARACTER TEXT(20)*4,BUF(22)*4
      CHARACTER CCH(4)*1
      COMMON /HERSH/ ITYP,CCH
      EQUIVALENCE (IDUMMY(1),BUF(1))
C
      IF (ITYP.LT.1.OR.ITYP.GT.4) ITYP=1
      IF (N.LT.0) THEN
           CALL CGXSMB(X,Y,H,TEXT,THETA,N)
      ELSE
           N4=(N-1)/4+1
           WRITE(BUF,200) CCH(ITYP),(TEXT(J),J=1,N4),':'
 200           FORMAT(A1,20A4,A1)
           CALL CGXSMB(X,Y,H,BUF,THETA,N+1)
           END IF
      RETURN
      END
C
C ****************************************************************
C
c      SUBROUTINE BLCKX6
      block data BLCKX6
      CHARACTER CCH(4)*1
      COMMON /HERSH/ ITYP,CCH
      DATA CCH /'%',' ','&','!'/
      END
C
C ****************************************************************
C
      SUBROUTINE CGXPIN(X,Y,H,NUMB,THETA,IW)
C     PLOTS AN INTEGER NUMBER EQUIVALENTLY TO THE
C     FORTRAN I - FORMAT.
C
      CHARACTER BUF(80)*4
      CHARACTER*1 FORMI(6),FORMF(10),FORME(10),FORMG(10)
      DATA FORMI / '(','I',' ',' ',')',' ' /
      DATA FORMF / '(','F',' ',' ','.',' ',' ',' ',')',' ' /
      DATA FORME / '(','E',' ',' ','.',' ',' ',' ',')',' ' /
      DATA FORMG / '(','G',' ',' ','.',' ',' ',' ',')',' ' /
C
 100  FORMAT(I2)
      WRITE(FORMI(3),100) IW
      WRITE(BUF,FORMI) NUMB
      GO TO 99
C
C ---------------------------------------------------
C
      ENTRY CGXPFN(X,Y,H,FNUMB,THETA,IW,ID)
C     PLOTS A FLOATING NUMBER EQUIVALENTLY TO THE
C     FORTRAN F - FORMAT.
C
      WRITE(FORMF(3),100) IW
      WRITE(FORMF(6),100) ID
      WRITE(BUF,FORMF) FNUMB
      GO TO 99
C
C ---------------------------------------------------
C
      ENTRY CGXPEN(X,Y,H,FNUMB,THETA,IW,ID)
C     PLOTS A FLOATING NUMBER EQUIVALENTLY TO THE
C     FORTRAN E - FORMAT.
C
      WRITE(FORME(3),100) IW
      WRITE(FORME(6),100) ID
      WRITE(BUF,FORME) FNUMB
      GO TO 99
C
C ---------------------------------------------------
C
      ENTRY CGXPGN(X,Y,H,FNUMB,THETA,IW,ID)
C     PLOTS A FLOATING NUMBER EQUIVALENTLY TO THE
C     FORTRAN G - FORMAT.
C
      WRITE(FORMG(3),100) IW
      WRITE(FORMG(6),100) ID
      WRITE(BUF,FORMG) FNUMB
 99   CALL CGXSMX(X,Y,H,BUF,THETA,IW)
      RETURN
      END
C
C ****************************************************************
C
      SUBROUTINE CGXNUM(X,Y,H,FNUMB,THETA,ID)
      IF (ID.GE.0) THEN
           ANUMB=FNUMB+SIGN(.5/10.**ID,FNUMB)
      ELSE
           ANUMB=FNUMB+SIGN(.5,FNUMB)
           END IF
      IW=IFIX(ALOG10(ABS(ANUMB)))+1
      IF (IW.LE.0) IW=1
      IF (ANUMB.LT.0) IW=IW+1
      IF (ID.GE.0) THEN
           IW=IW+ID+1
           CALL CGXPFN(X,Y,H,FNUMB,THETA,IW,ID)
      ELSE
           CALL CGXPIN(X,Y,H,IFIX(ANUMB),THETA,IW)
           END IF
      RETURN
      END
C
C ****************************************************************
C
      SUBROUTINE CGXPLT(X,Y,IVAL)
C     ***INTERFACE TO DISPLA
      IF (IVAL.EQ.2) THEN
           CALL BLXDRW(X,Y)
      ELSEIF (IVAL.EQ.3) THEN
           CALL BLXMOV(X,Y)
      ELSE
           STOP 'CGXPLT ERROR'
           ENDIF
      RETURN
      END
