C
C     ***DATA AREAS TO HOLD PLOT NAMES FOR MINDIS
C     *** JOHN STEIERT   3-DEC-81
C
C          PLTTL         PLOT TITLE
C          PLTTLL        LENGTH OF PLOT TITLE
C          PLXLAB        LABEL FOR X AXIS
C          PLXLBL        LENGTH OF LABEL FOR X AXIS
C          PLYLAB        LABEL FOR Y AXIS
C          PLYLBL        LENGTH OF LABEL FOR Y AXIS
C          PLHDG(4)      HEADING STORAGE AREA
C          PLHDGL(4)     LENGTH OF LINES IN HEADING
C          HDGLNS        NUMBER OF LINES IN HEADING
C          HDGULS(4)     NUMBER OF TIMES LINES TO BE UNDERLINED
C          HDGHGT(4)     HEIGHT OF CHARS IN LINE
C          HDGCLI        CURRENT LINE NUMBER
C          PLTTIM        TIME OF CURRENT PLOT
C          PLTDAT        DATE OF CURRENT PLOT
C          PLTFNM        FILENAME BASIS: IE DATE+TIME
C          PLTRID        RUN IDENTIFICATION FOR CURRENT JOB
C          PLTRIL        LENGTH OF RUN IDENTIFICATION
C
      CHARACTER*72 PLTTL, PLXLAB, PLYLAB, PLHDG(4)
      INTEGER PLTTLL, PLXLBL, PLYLBL, PLHDGL(4)
      INTEGER HDGLNS, HDGULS(4), HDGHGT(4), HDGCLI
      CHARACTER*8 PLTTIM, PLTDAT
      CHARACTER*10 PLTFNM
      CHARACTER*6 PLTRID
      INTEGER PLTRIL
      COMMON /NAM$DT/ PLTTL,  PLXLAB, PLYLAB, PLHDG,
     $                PLTTLL, PLXLBL, PLYLBL, PLHDGL,
     $                HDGLNS, HDGULS, HDGHGT, HDGCLI,
     $                PLTTIM, PLTDAT, PLTFNM, PLTRID,
     $                PLTRIL
